package javaPractice.ch_04_if;

public class Test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		if ( a > 3 ) { //if 조건식
			
			//조건식이 참일 때 실행
			System.out.println("a는 3보다 큽니다.");
		} //if구문의 끝
		
		//조건식과 상관없이 무조건 실행
		System.out.println("검사가 끝났습니다.");
		
	}

}
